import { StatusBar } from 'expo-status-bar';
import { useEffect, useState } from 'react';
import { Button, StyleSheet, Text, View, TextInput, Image, TouchableOpacity, TouchableWithoutFeedback } from 'react-native';
import Logo from './assets/logo.png'
import CustomInput from './components/CustomInput';
import styles from './styles/styles';
import app from './firebaseConfig';
import { getAuth, createUserWithEmailAndPassword } from '@firebase/auth';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator} from '@react-navigation/native-stack';

const Stack = createNativeStackNavigator

export default function App() {
  
  const auth = getAuth(app);

  const [email, setEmail] = useState()
  const [senha, setSenha] = useState()

  useEffect(() => {

  }, [auth.currentUser])
 
  const setEmailInputValue = (value) => {
    setEmail(value)
  }

  const setSenhaInputValue = (value) => {
    setSenha(value)
  }

  const criarUsuario = () => {
    createUserWithEmailAndPassword(auth, email, senha)
    .then((userCredentials) => {
      console.log(userCredentials)
    }).catch((err) => {
      console.log()
    })
  }

  const singIn = () => {
    singInWithEmailAndPassword(auth, email, senha)
    .then((userCredentials) => {
      console.log(userCredentials)
    }).catch((error) => {
      console.log(error)
    })
  }

  return (
    <View style={styles.container}>
      <Image
        source={Logo}
        style={{
          width: 100,
          height: 100
        }}
      />

      <CustomInput
        placeholder='E-mail'
        value={email} 
        funcao={setEmailInputValue}
      />

      <CustomInput
        placeholder='Senha'
        value={senha}
        funcao={setSenhaInputValue}
        isPassword={true}
      />

      <TouchableOpacity style={{ marginTop: 15 }} onPress={criarUsuario}>
        <View style={styles.button}>
          <Text style={styles.buttonText}>Entrar</Text>
        </View>
      </TouchableOpacity>
    </View>
  );
}
