import { StatusBar } from 'expo-status-bar';
import { useEffect, useState } from 'react';
import { Button, StyleSheet, Text, View, TextInput, Image, TouchableOpacity, TouchableWithoutFeedback } from 'react-native';
import Logo from '../assets/logo.png'
import CustomInput from '../components/CustomInput';
import styles from '../styles/styles';
import app from '../firebaseConfig';
import { getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword } from '@firebase/auth';
import { useNavigation } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

export default function Cadastro() {
    const [Valor, setValor] = useState()
    const [Referencia, setReferencia] = useState()
    const [Data, setData] = useState()

    const navigation = useNavigation()

    const setValorInputValue = (value) => {
        setReferencia(value)
    }

    const setReferenciaInputValue = (value) => {
        setValor(value)
    }

    const setDataInputValue = (value) => {
        setData(value)
    }

    const criarUsuario = () => {
        createUserWithEmailAndPassword(auth, email, senha)
            .then((dadosDoUsuario) => {
                console.log(dadosDoUsuario)
            }).catch((err) => {
                console.error(err)
            })
    }

    const CadastrarTranferecia = () => {
        CadastrarTranfereciaNaPage(Valor, Referencia, Data)
            .then((DadosdaTranferencia) => {
                console.log(DadosdaTranferencia)
            }).catch((err) => {
                console.error(err)
            })
    }

    return (
        <View style={styles.container}>
            {/* <Image
                source={Logo}
                style={{
                    width: 100,
                    height: 100
                }}
            /> */}

            <CustomInput
                placeholder='Valor'
                value={valor}
                funcao={setValorInputValue}
            />

            <CustomInput
                placeholder='Referencia'
                value={referencia}
                funcao={setValorInputValue}
            />

            <CustomInput
                placeholder='Data'
            />
            <TouchableOpacity style={{ marginTop: 15 }} onPress={CadastrarTranferecia}>
                <View style={styles.button}>
                    <Text style={styles.buttonText}>Entrar</Text>
                </View>
            </TouchableOpacity>
        </View>
    );
}
